import React from 'react';
import './App.css';


const App = () => {
  return (
    <div className='display-flex flex-column min-h-screen bg-gray-800'>
      {/* Navigation Bar Using Tailwind CSS and Router DOM*/}
      <nav className='bg-gray-900 text-white p-4'>
        <div className='container mx-auto flex justify-between items-center'>
          <div className='text-2xl font-bold'>User Management</div>
          <div>
            <a href="/login" className='mr-4 hover:underline'>
            Login</a>
            <a href="/signup" className='hover:underline'>Signup</a>
          </div>
        </div>
      </nav>
      {/* Main Content Area */}
      <main className='flex-grow container mx-auto p-4 text-white'>
        <h1 className='text-3xl font-semibold mb-4'>Welcome to the User Management System</h1>
        <p className='text-lg'>Please login or signup to continue.</p>
      </main>
      {/* Footer */}
      <footer className='bg-gray-900 text-white p-4 text-center'>
        &copy; 2024 User Management System. All rights reserved.
      </footer>


    </div>
  )
}

export default App;
